import React from 'react'; 
import OrangeButton from './OrangeButton';
import styles from './styles';
import {Text, View,} from 'react-native';
import MenuTab from './MenuTab';
const items = ['Today','This month','In 7 days'];
function onMenuTabClicked(index){
    console.log('display'+items[index]);
}
export default function MainScreen(props){
    return(
        <View style={styles.container}>
            <Text style={styles.title}>Dashboard</Text>
            <OrangeButton title="pay your expense"/>
            <OrangeButton title="shedule payment"/>
            <MenuTab  items={items} onclick={onMenuTabClicked}/>
        </View>
    );
}
