import React from 'react';
import style from './styles';
import {View,Text,StyleSheet} from 'react-native';
export default function MenuTab(props){
    return(
        <View style={style.tabMenu}> 
        {
            props.items.map((item,index)=><Text style={index==0?
                [style.tabMenuItems,style.tabMenuItemSelected]:style.tabMenuItems}
                 onPress = {(item)=>{
                     console.log(this);
                     item.style=style.tabMenuItemSelected;
                     props.onclick(index);
                    }}
                >{item}</Text>)
        }
        </View>
    );
}
